var instanciaPrimaria = current.variables.instancia_primaria_bcr;
var nombreVirtualServer2 = current.variables.nombre_del_servidor_virtual;
var puerto = current.variables.tipo_de_puerto;
var servicegroupname = "SG_" + nombreVirtualServer2 + "_" + puerto;

try {
    var r = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'BindServiceGrouptoVS');

    r.setStringParameter("ipAddress", instanciaPrimaria);

    var jsonData = '{"lbmonitor_servicegroup_binding":{"monitorname":"tcp","servicegroupname":"' + servicegroupname + '","state":"ENABLED"}}';
    r.setRequestBody(jsonData);

    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();
    if (httpStatus == 200 || httpStatus == 201) {
        current.work_notes = "La operacion de monitoreo del Service Group se ha completado.";

    } else {

        current.work_notes = "Error (" + httpStatus + "): La operación de monitoreo del ServiceGroup a fallado. Detalles: " + responseBody;
    }
} catch (ex) {
    var message ="Error al monitorear el Service Group: "+ ex.message;
	current.work_notes = message;
}

