var queDeseasHacer = current.variables.que_deseas_hacer;


if (queDeseasHacer == 'Agregar más servidores') {


    if (catidadServiceGroupEnlazar > 0) {
        if (catidadServiceGroupEnlazar == 1) {
            grupo = [
                [nombreServidor1, ipServidor1]
            ];
            //ServidorUno, virtual serverUno
        } else if (catidadServiceGroupEnlazar == 2) {
            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2]
            ];
            //servidor1, virtual server1, servidor2, virtualserver2
        } else if (catidadServiceGroupEnlazar == 3) {
            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3]
            ];
        } else if (catidadServiceGroupEnlazar == 4) {
            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3],
                [nombreServidor4, ipServidor4]
            ];
        } else if (catidadServiceGroupEnlazar == 5) {

            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3],
                [nombreServidor4, ipServidor4],
                [nombreServidor5, ipServidor5]
            ];

        } else if (catidadServiceGroupEnlazar == 6) {
            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3],
                [nombreServidor4, ipServidor4],
                [nombreServidor5, ipServidor5],
                [nombreServidor6, ipServidor6]
            ];

        } else if (catidadServiceGroupEnlazar == 7) {

            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3],
                [nombreServidor4, ipServidor4],
                [nombreServidor5, ipServidor5],
                [nombreServidor6, ipServidor6],
                [nombreServidor7, ipServidor7]
            ];

        } else if (catidadServiceGroupEnlazar == 8) {

            grupo = [
                [nombreServidor1, ipServidor1],
                [nombreServidor2, ipServidor2],
                [nombreServidor3, ipServidor3],
                [nombreServidor4, ipServidor4],
                [nombreServidor5, ipServidor5],
                [nombreServidor6, ipServidor6],
                [nombreServidor7, ipServidor7],
                [nombreServidor8, ipServidor8]
            ];
        }

    }

    for (var i = 0; i < grupo.length; i++) {

        var x = grupo[i];
        var n = x.toString();
        var array = n.split(',');
        //current.work_notes = "server = [:"+array[0] + "]          IP Address[ "+array[1]+"]";


        try {
            var r = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'CrearServerBCR30');
            r.setStringParameter("ipAddress", instanciaPrimaria);
            //JSON
            var jsonString = '{"server":{"name":"' + array[0] + '","ipaddress":"' + array[1] + '"}}';
            r.setRequestBody(jsonString);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            if (httpStatus == 200 || httpStatus == 201) {
                current.work_notes = "La operación agregar  "+array[0]+" con ip "+array[1]+" se ha completado.";
            } else {
                current.work_notes = "Error (" + httpStatus + "): La operación agregar  "+array[0]+" con ip "+array[1]+" ha  fallado. Detalles:" + responseBody;
            }

        } catch (ex) {
            var message = ex.message;
			current.work_notes = "Error al agregar Server : "+ message;
        }


    }

}

