if(catidadServiceGroupEnlazar > 0){
	if(catidadServiceGroupEnlazar == 1){
		grupo = [nombreServidor1];
	}else if(catidadServiceGroupEnlazar == 2){
		grupo = [nombreServidor1, nombreServidor2];
	}else if(catidadServiceGroupEnlazar == 3){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3];
	}else if(catidadServiceGroupEnlazar == 4){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3, nombreServidor4];
	}else if(catidadServiceGroupEnlazar == 5){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3, nombreServidor4,nombreServidor5];
	}else if(catidadServiceGroupEnlazar == 6){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3, nombreServidor4,nombreServidor5,nombreServidor6];
	}else if(catidadServiceGroupEnlazar == 7){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3, nombreServidor4,nombreServidor5,nombreServidor6,nombreServidor7];
	}else if(catidadServiceGroupEnlazar == 8){
		grupo = [nombreServidor1, nombreServidor2, nombreServidor3, nombreServidor4,nombreServidor5,nombreServidor6,nombreServidor7,nombreServidor8];
	}
	
	//{"servicegroup_servicegroupmember_binding":{"servicegroupname":"SG_VIRTUALSERVERCITRIX_80","servername":"citrixser0002","hashid":"1","state":"ENABLED","port":"80"}}
		for(var i = 0; i < grupo.length; i++){
			
			
			//var nombreVirtualServer = "VS_"+current.variables.nombre_del_servidor_virtual+"_"+puerto;
			try {
				var r = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'ServiceGroupMemberBinding BCR .30');
				
				r.setStringParameter("ipAddress",instanciaPrimaria);
				var stringData = '{"servicegroup_servicegroupmember_binding":{"servicegroupname":"'+servicegroupname+'","servername":"'+grupo[i]+'","hashid":"1","state":"ENABLED","port":"'+puerto+'"}}';
					r.setRequestBody(stringData);
					var response = r.execute();
					var responseBody = response.getBody();
					var httpStatus = response.getStatusCode();
					
				if( httpStatus == 200 || httpStatus == 201){
						current.work_notes = "La operacion enlazar servidores al Service Group se ha completado.";
						
					}else{
						
						current.work_notes = "Error ("+httpStatus+"): La operacion enlazar servidores al Service Group ha  fallado. Detalles: "+responseBody;
						
						
					}
			}
					catch(ex) {
						var message ="Error al enlazar servidores al ServiceGroup:"+ ex.message;
						current.work_notes=message;
					}
				}
			}
			
			
			
