var numeroServidoresHabilitar = current.variables.n_mero_de_servidores_a_Habilitar;
var servidor1 = current.variables.servidor_a_eliminar_1;
var servidor2 = current.variables.servidor_a_eliminar_2;
var servidor3 = current.variables.servidor_a_eliminar_3;
var servidor4 = current.variables.servidor_a_eliminar_4;
var grupo = [];
var queDeseasHacer = current.variables.que_deseas_hacer;


if (queDeseasHacer == 'Habilitar Servidores') {

    //valido si es crear server, deshabilitar o eliminar
    //******************CREACIÓN binding*****************

    if (numeroServidoresHabilitar > 0) {

        if (numeroServidoresHabilitar == 1) {
            grupo = [servidor1];
            //ServidorUno, virtual serverUno


        } else if (numeroServidoresHabilitar == 2) {
            grupo = [servidor1, servidor2];
            //servidor1, virtual server1, servidor2, virtualserver2

        } else if (numeroServidoresHabilitar == 3) {
            grupo = [servidor1, servidor2, servidor3];


        } else if (numeroServidoresHabilitar == 4) {
            grupo = [servidor1, servidor2, servidor3, servidor4];

        }
    }

    for (var i = 0; i < grupo.length; i++) {


        try {
            var r = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'HabilitarServidoresdeGranja');
            r.setStringParameter("ipAddress", instanciaPrimaria);
            var string1 = '{"server":{"name":"' + grupo[i] + '"}}';
            r.setRequestBody(string1);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            if (httpStatus == 200 || httpStatus == 201) {
                current.work_notes = "La operación habilitar Server se ha completado.";
            } else {
                current.work_notes = "Error (" + httpStatus + "): La operación habilitar Server ha  fallado. Detalles:" + responseBody;
            }

        } catch (ex) {
            var message = ex.message;
			current.work_notes = "Error al habilitar Server: " + message;
        }


    }
}

