//
// For example:
//     task.short_description = current.short_description;


task.description = current.variables.descripci_n_de_la_solicitud;
task.opened_by = current.variables.solicitud_para;

var array = [];
var ServiceGroupname = "";
servidoresenServiceGroup = [];
var finIP = "";
var virtualName = "";
var PuertoServer = "";
var parametroSelectBox = current.variables.granja_a_consultar;
var instanciaPrimaria = current.variables.instancia_primaria_bcr;
var servidores = "";

var r = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'ConsultaGranjaBCR');

r.setStringParameter("serverName", parametroSelectBox);
r.setStringParameter("ipAddress", instanciaPrimaria);

var response = r.execute();
var responseBody = response.getBody();
var httpStatus = response.getStatusCode();
var arrayValue = JSON.parse(responseBody).lbvserver_binding;

for (var i = 0; i < arrayValue.length; i++) {

    ServiceGroupname = arrayValue[i].lbvserver_servicegroup_binding[i].servicegroupname;
    virtualName = arrayValue[i].lbvserver_servicegroup_binding[i].name;

    try {
        var m = new sn_ws.RESTMessageV2('x_287988_bcr_app.BCRmessage', 'ServiceGroupMemberBindingGetBCR .30');
        m.setStringParameter("serverGroup", ServiceGroupname);
        m.setStringParameter("ipAddress", instanciaPrimaria);
        var res = m.execute();
        var responseBody2 = res.getBody();
        var httpStatus2 = res.getStatusCode();
        var Servers = JSON.parse(responseBody2).servicegroup_servicegroupmember_binding;


        for (var j = 0; j < Servers.length; j++) {
            servidores += '\n' + Servers[j].servername + ' (' + Servers[j].ip + ':'+Servers[j].port+')';
        }

    } catch (ex) {
        var message = ex.message;
        current.work_notes = message;
    }

    current.work_notes = "La información del Virtual Server consultada es la siguiente:" +
        "\n1- Virtual Server: " + virtualName +
        "\n2- ServiceGroup: " + ServiceGroupname +
        "\n3- Servidores: " + servidores.toString();
  


    // con un split lo saco de los arrays
}

